package main__test

import (
	"fmt"
	"testing"
	"tools/rsa2"
)

func TestRsa2(t *testing.T) {
	rsa2Ver := new(rsa2.Rsa2Verify)
	rsa2Ver.PubKey = ""
	rsa2Ver.PrivateKey = ""

	dataStr := "{\"data_key\":\"JC2021111601\",\"data_value\":{\"name\":\"贵宾卡\",\"price\":100.00}}"
	sign, err := rsa2Ver.CreateSign(dataStr)
	fmt.Println("错误信息：", err)
	fmt.Println("签名：", sign)
	err = rsa2Ver.VerifySign(dataStr, sign)
	if err != nil {
		fmt.Println("错误信息：", err)
	} else {
		fmt.Println("验签成功")
	}

}
